tinymce.PluginManager.add('upload', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('upload', {
        text: 'Upload',
        icon: false,
		tooltip: 'Upload',
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Upload',
                body: [
                    {
                        type: 'label',
                        name: 'tip1',
                        label: 'Currently you can upload your images, videos and files here:'
                    },
                    {
                        type: 'label',
                        name: 'tip2',
                        label: 'http://www.picofile.com'
                    },
                    {
                        type: 'label',
                        name: 'tip3',
                        label: 'Press OK to open this website or Cancel...'
                    }
                ],
                onsubmit: function(e) {
                    window.open('http://www.picofile.com');
                }
            });
        }
    });
});